import React, { useState } from 'react';

function Todo({ task, done, onClick }) {
    return (
        <li
            style={{ textDecoration: done ? 'line-through' : 'none' }}
            onClick={onClick}
        >
            {task}
        </li>
    );
}

function TodoList({ todos, onTodoClick, onSearch, onFilter }) {
    const [tasks, setTasks] = useState([]);
    const [taskInput, setTaskInput] = useState("");

    const [searchTerm, setSearchTerm] = useState('');
    const [filter, setFilter] = useState('all');

    const filteredTodos = todos
        .filter(todo =>
            filter === 'all' ||
            (filter === 'done' && todo.done) ||
            (filter === 'not-done' && !todo.done)
        )
        .filter(todo => todo.task.includes(searchTerm));

    return (
        <div>
            <input
                type="text"
                value={searchTerm}
                onChange={event => setSearchTerm(event.target.value)}
                onKeyUp={event => onSearch(event.target.value)}
            />
            <select value={filter} onChange={event => setFilter(event.target.value)}>
                <option value="all">All</option>
                <option value="done">Done</option>
                <option value="not-done">Not done</option>
            </select>
            <ul>
                {filteredTodos.map(todo => (
                    <Todo key={todo.task} {...todo} onClick={() => onTodoClick(todo.task)} />
                ))}
            </ul>
        </div>
    );
}

function App() {
    const [todos, setTodos] = useState([
        { task: 'get milk', done: false },
        { task: 'get bread', done: false },
        { task: 'get eggs', done: false },
    ]);

    const toggleTodo = task =>
        setTodos(
            todos.map(todo =>
                todo.task === task ? { ...todo, done: !todo.done } : todo
            )
        );

    const handleSearch = term => setTodos(todos.filter(todo => todo.task.includes(term)));

    return (
        <TodoList todos={todos} onTodoClick={toggleTodo} onSearch={handleSearch} />
    );
}

export default App;
