import React, { useState } from "react";

function ToDoList() {
    const [tasks, setTasks] = useState([]);
    const [taskInput, setTaskInput] = useState("");
    const [searchTerm, setSearchTerm] = useState("");

    function handleSubmit(e) {
        e.preventDefault();
        setTasks([...tasks, { task: taskInput, done: false }]);
        setTaskInput("");
    }

    function handleDone(index) {
        const updatedTasks = [...tasks];
        updatedTasks[index].done = true;
        setTasks(updatedTasks);
    }

    const filteredTasks = tasks.filter((task) => task.task.toLowerCase().includes(searchTerm.toLowerCase()));

    return (
        <div>
            <h1>To-Do List</h1>
            <form onSubmit={handleSubmit}>
                <input
                    type="text"
                    value={taskInput}
                    onChange={(e) => setTaskInput(e.target.value)}
                />
                <button type="submit">Add Task</button>
            </form>
            <br />
            <input
                type="text"
                placeholder="Search tasks..."
                value={searchTerm}
                onChange={(e) => setSearchTerm(e.target.value)}
            />
            <ul>
                {filteredTasks.map((task, index) => (
                    <li key={index} style={{ textDecoration: task.done ? "line-through" : "none" }}>
                        {task.task}{" "}
                        {!task.done && (
                            <button onClick={() => handleDone(index)}>Done</button>
                        )}
                    </li>
                ))}
            </ul>
        </div>
    );
}

export default ToDoList;
